import React from 'react';

const formatDate = (value) => (value ? new Date(value).toLocaleDateString() : 'Ongoing');
const humanizeStatus = (value) =>
  value ? value.replace(/-/g, ' ').replace(/\b\w/g, (char) => char.toUpperCase()) : 'Unknown';

function ContractSnapshot({ contracts = [] }) {
  return (
    <div className="card contract-card">
      <div className="card-heading">
        <h3>ü§ù Contract Snapshot</h3>
        <p>Active horse group contracts & renewal windows</p>
      </div>
      {contracts.length > 0 ? (
        <div className="contract-list">
          {contracts.map((contract) => (
            <div key={contract.id} className="contract-row">
              <div>
                <p className="contract-name">{contract.contract_name}</p>
                <p className="contract-meta">
                  {contract.group_name} ¬∑ {formatDate(contract.start_date)} ‚Äì {formatDate(contract.end_date)}
                </p>
              </div>
              <span className={`contract-pill contract-pill-${contract.status || 'active'}`}>
                {humanizeStatus(contract.status)}
              </span>
            </div>
          ))}
        </div>
      ) : (
        <div className="empty-state">
          <p>No active contracts running right now.</p>
        </div>
      )}
    </div>
  );
}

export default ContractSnapshot;
