import React from 'react';

function GuestValueSpotlight({ guests = [] }) {
  const sorted = [...(guests || [])]
    .sort((a, b) => (b.total_revenue || 0) - (a.total_revenue || 0))
    .slice(0, 3);

  const getBadge = (visits) => {
    if (visits >= 5) return 'VIP';
    if (visits >= 3) return 'Loyal';
    if (visits >= 2) return 'Returning';
    return 'New';
  };

  return (
    <div className="guest-spotlight-card">
      <div className="section-header">
        <h3>Guest Value Spotlight</h3>
        <p>Top repeat guests by lifetime revenue.</p>
      </div>
      <div className="spotlight-grid">
        {sorted.length > 0 ? (
          sorted.map((guest, index) => (
            <div key={guest.guest_name + index} className="spotlight-item">
              <div className="spotlight-rank">#{index + 1}</div>
              <div>
                <h4>{guest.guest_name}</h4>
                <div className="spotlight-meta">
                  <span>{guest.visit_count} visits</span>
                  <span>${guest.total_revenue?.toFixed(2) || '0.00'} total</span>
                </div>
                <span className={`badge badge-${getBadge(guest.visit_count).toLowerCase()}`}>{getBadge(guest.visit_count)}</span>
              </div>
            </div>
          ))
        ) : (
          <p className="empty-state small">No repeat guests yet. Once someone returns, they will light up here.</p>
        )}
      </div>
    </div>
  );
}

export default GuestValueSpotlight;
