import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import './styles/InsightsExtras.css';
import Dashboard from './components/Dashboard';
import BookingForm from './components/BookingForm';
import BookingsTable from './components/BookingsTable';
import ReturnGuestAnalysis from './components/ReturnGuestAnalysis';
import AreaUtilization from './components/AreaUtilization';

const BOOKING_STATUS_OPTIONS = [
  { value: 'active', label: 'Active' },
  { value: 'confirmed', label: 'Confirmed' },
  { value: 'checked-in', label: 'Checked In' },
  { value: 'checked-out', label: 'Checked Out' },
  { value: 'canceled', label: 'Canceled' },
  { value: 'no-show', label: 'No-Show' }
];

function App() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [bookings, setBookings] = useState([]);
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);
  const [statusSummary, setStatusSummary] = useState([]);
  const [arrivalRadar, setArrivalRadar] = useState([]);
  const [guestTypeData, setGuestTypeData] = useState([]);
  const [guestSpotlight, setGuestSpotlight] = useState([]);
  const [contracts, setContracts] = useState([]);
  const [dateRange, setDateRange] = useState({
    startDate: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
    endDate: new Date().toISOString().split('T')[0]
  });

  const fetchCoreData = async () => {
    setLoading(true);
    try {
      const [bookingsRes, summaryRes] = await Promise.all([
        axios.get('/api/bookings', { params: dateRange }),
        axios.get('/api/dashboard/summary', { params: dateRange })
      ]);
      setBookings(bookingsRes.data || []);
      setSummary(summaryRes.data || {});
    } catch (error) {
      console.error('Error fetching core data:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDashboardExtras = async () => {
    try {
      const [statusRes, radarRes, guestTypeRes, guestSpotlightRes, contractsRes] = await Promise.all([
        axios.get('/api/bookings/status-summary', { params: dateRange }),
        axios.get('/api/bookings/arrival-radar'),
        axios.get('/api/bookings/by-guest-type', { params: dateRange }),
        axios.get('/api/return-guests'),
        axios.get('/api/contracts/active')
      ]);
      setStatusSummary(statusRes.data || []);
      setArrivalRadar(radarRes.data || []);
      setGuestTypeData(guestTypeRes.data || []);
      setGuestSpotlight((guestSpotlightRes.data || []).slice(0, 3));
      setContracts(contractsRes.data || []);
    } catch (error) {
      console.error('Error fetching dashboard extras:', error);
    }
  };

  const refreshDashboard = () => {
    fetchCoreData();
    fetchDashboardExtras();
  };

  useEffect(() => {
    refreshDashboard();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [dateRange]);

  const handleAddBooking = async (bookingData) => {
    try {
      await axios.post('/api/bookings', bookingData);
      refreshDashboard();
      alert('Booking added successfully! üéâ');
    } catch (error) {
      console.error('Error adding booking:', error);
      alert('Failed to add booking');
    }
  };

  const handleDeleteBooking = async (id) => {
    if (window.confirm('Are you sure you want to delete this booking?')) {
      try {
        await axios.delete(`/api/bookings/${id}`);
        refreshDashboard();
        alert('Booking deleted');
      } catch (error) {
        console.error('Error deleting booking:', error);
      }
    }
  };

  const handleStatusUpdate = async (bookingId, newStatus) => {
    const booking = bookings.find((b) => b.id === bookingId);
    if (!booking) return;
    try {
      await axios.put(`/api/bookings/${bookingId}`, {
        ...booking,
        status: newStatus
      });
      refreshDashboard();
    } catch (error) {
      console.error('Error updating booking status:', error);
      alert('Unable to update booking status right now');
    }
  };

  return (
    <div className="app">
      {/* Header */}
      <header className="app-header">
        <div className="header-content">
          <h1>‚ú® Campsite Sales Dashboard ‚ú®</h1>
          <p>Track bookings, revenue & guest relationships</p>
        </div>
      </header>

      {/* Navigation */}
      <nav className="main-nav">
        <button
          className={`nav-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
          onClick={() => setActiveTab('dashboard')}
        >
          üìä Dashboard
        </button>
        <button
          className={`nav-btn ${activeTab === 'bookings' ? 'active' : ''}`}
          onClick={() => setActiveTab('bookings')}
        >
          üìÖ Bookings
        </button>
        <button
          className={`nav-btn ${activeTab === 'add' ? 'active' : ''}`}
          onClick={() => setActiveTab('add')}
        >
          ‚ûï New Booking
        </button>
        <button
          className={`nav-btn ${activeTab === 'returns' ? 'active' : ''}`}
          onClick={() => setActiveTab('returns')}
        >
          üîÑ Return Guests
        </button>
        <button
          className={`nav-btn ${activeTab === 'areas' ? 'active' : ''}`}
          onClick={() => setActiveTab('areas')}
        >
          üèïÔ∏è Area Usage
        </button>
      </nav>

      {/* Date Range Selector */}
      <div className="date-range-selector">
        <div className="date-input-group">
          <label>From:</label>
          <input
            type="date"
            value={dateRange.startDate}
            onChange={(e) => setDateRange({ ...dateRange, startDate: e.target.value })}
          />
        </div>
        <div className="date-input-group">
          <label>To:</label>
          <input
            type="date"
            value={dateRange.endDate}
            onChange={(e) => setDateRange({ ...dateRange, endDate: e.target.value })}
          />
        </div>
        <button className="refresh-btn" onClick={refreshDashboard} disabled={loading}>
          {loading ? '‚è≥ Loading...' : 'üîÑ Refresh'}
        </button>
      </div>

      {/* Main Content */}
      <main className="app-content">
        {activeTab === 'dashboard' && (
          <Dashboard
            summary={summary}
            bookings={bookings}
            loading={loading}
            statusSummary={statusSummary}
            arrivalRadar={arrivalRadar}
            guestValueSpotlight={guestSpotlight}
            guestTypeData={guestTypeData}
            contracts={contracts}
          />
        )}
        {activeTab === 'bookings' && (
          <BookingsTable
            bookings={bookings}
            statusOptions={BOOKING_STATUS_OPTIONS}
            onDelete={handleDeleteBooking}
            onStatusChange={handleStatusUpdate}
            loading={loading}
          />
        )}
        {activeTab === 'add' && (
          <BookingForm onSubmit={handleAddBooking} statusOptions={BOOKING_STATUS_OPTIONS} />
        )}
        {activeTab === 'returns' && (
          <ReturnGuestAnalysis dateRange={dateRange} />
        )}
        {activeTab === 'areas' && (
          <AreaUtilization dateRange={dateRange} />
        )}
      </main>

      {/* Footer */}
      <footer className="app-footer">
        <p>Built with üíï for amazing campsites</p>
      </footer>
    </div>
  );
}

export default App;
