import React from 'react';
import '../styles/ArrivalRadar.css';

const STATUS_PALATE = {
  active: 'pill-active',
  confirmed: 'pill-confirmed',
  'checked-in': 'pill-checked-in',
  'checked-out': 'pill-checked-out',
  canceled: 'pill-canceled',
  'no-show': 'pill-noshow'
};

function ArrivalRadar({ arrivals = [], loading, compact = false, limit = 7, title = 'Arrival Radar' }) {
  if (loading && (!arrivals || arrivals.length === 0)) {
    return <div className="arrival-card loading">‚è≥ Loading arrivals...</div>;
  }

  const displayed = arrivals.slice(0, limit);

  return (
    <div className={`arrival-card ${compact ? 'compact' : ''}`}>
      <div className="arrival-header">
        <h3>üö® {title}</h3>
        <span className="arrival-meta">Next {displayed.length} bookings</span>
      </div>

      {displayed.length > 0 ? (
        <div className="arrival-table-wrapper">
          <table>
            <thead>
              <tr>
                <th>Arrival</th>
                <th>Guest</th>
                <th>Area</th>
                <th>Nights</th>
                <th>Departure</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {displayed.map((booking) => (
                <tr key={booking.id}>
                  <td>{new Date(booking.booking_date).toLocaleDateString()}</td>
                  <td><strong>{booking.guest_name}</strong></td>
                  <td>{booking.area_rented}</td>
                  <td>{booking.nights}</td>
                  <td>{new Date(booking.departure_date).toLocaleDateString()}</td>
                  <td>
                    <span className={`arrival-status-pill ${STATUS_PALATE[booking.status] || ''}`}>
                      {booking.status?.replace('-', ' ')?.toUpperCase()}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="empty-text">No upcoming arrivals found.</p>
      )}
    </div>
  );
}

export default ArrivalRadar;
