import React from 'react';

function ContractSnapshot({ contracts = [] }) {
  return (
    <div className="contract-snapshot">
      <div className="section-header">
        <h3>Contract Snapshot</h3>
        <p>Active horse group contracts & renewal windows.</p>
      </div>
      {contracts.length > 0 ? (
        <div className="contract-grid">
          {contracts.map(contract => (
            <div key={contract.id} className="contract-card">
              <div className="contract-headline">
                <h4>{contract.contract_name}</h4>
                <span className="contract-status">{contract.status}</span>
              </div>
              <p className="contract-group">{contract.group_name}</p>
              <p className="contract-dates">{contract.start_date} â†’ {contract.end_date || 'Ongoing'}</p>
              <div className="contract-values">
                <span>Base: ${contract.base_monthly_rate?.toFixed(2) || '0.00'}</span>
                <span>Per guest: ${contract.per_guest_rate?.toFixed(2) || '0.00'}</span>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="empty-state small">No active contracts found yet.</p>
      )}
    </div>
  );
}

export default ContractSnapshot;
