import React from 'react';

const STATUS_META = [
  { key: 'active', label: 'Active', icon: 'âš¡', accent: 'var(--primary)' },
  { key: 'confirmed', label: 'Confirmed', icon: 'ðŸ“', accent: 'var(--accent)' },
  { key: 'checked-in', label: 'Checked-In', icon: 'ðŸšª', accent: 'var(--primary-dark)' },
  { key: 'checked-out', label: 'Checked-Out', icon: 'ðŸ', accent: 'var(--secondary)' },
  { key: 'canceled', label: 'Canceled', icon: 'âŒ', accent: 'var(--warning)' },
  { key: 'no-show', label: 'No-Show', icon: 'âš ï¸', accent: 'var(--border-color)' }
];

function StatusSummary({ summary = [] }) {
  const counts = (summary || []).reduce((acc, entry) => {
    acc[entry.status] = entry.count;
    return acc;
  }, {});

  return (
    <div className="status-summary-card">
      <div className="status-summary-header">
        <h3>Booking Status Tracker</h3>
        <p>Live counts keep you ahead of confirmations, cancellations, and check-ins.</p>
      </div>
      <div className="status-grid">
        {STATUS_META.map(meta => (
          <div className="status-card" key={meta.key} style={{ borderColor: meta.accent }}>
            <div className="status-icon" style={{ color: meta.accent }}>{meta.icon}</div>
            <div className="status-text">
              <span className="status-label">{meta.label}</span>
              <span className="status-count">{counts[meta.key] || 0}</span>
            </div>
            <p className="status-note">{(counts[meta.key] || 0)} bookings</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StatusSummary;
