import React from 'react';
import '../styles/GuestValueSpotlight.css';

const getBadgeLabel = (visitCount) => {
  if (visitCount > 3) return { label: 'VIP', color: 'vip' };
  if (visitCount > 1) return { label: 'Loyal', color: 'loyal' };
  return { label: 'Returning', color: 'base' };
};

function GuestValueSpotlight({ guests = [] }) {
  return (
    <div className="card guest-spotlight">
      <div className="section-header">
        <h3>ðŸŒŸ Guest Value Spotlight</h3>
        <p>Top return guests by lifetime revenue</p>
      </div>

      {guests.length > 0 ? (
        <div className="spotlight-list">
          {guests.map((guest, index) => {
            const badge = getBadgeLabel(guest.visit_count);
            return (
              <div className="spotlight-item" key={guest.guest_name + index}>
                <div>
                  <p className="spotlight-rank">#{index + 1}</p>
                  <h4>{guest.guest_name}</h4>
                  <p className="spotlight-meta">
                    {guest.visit_count} visits Â· ${guest.total_revenue?.toFixed(2) || '0.00'}
                  </p>
                </div>
                <span className={`loyalty-badge ${badge.color}`}>{badge.label}</span>
              </div>
            );
          })}
        </div>
      ) : (
        <div className="empty-state">
          <p>No return guests yet</p>
          <p className="hint">Repeat stays automatically populate this spotlight</p>
        </div>
      )}
    </div>
  );
}

export default GuestValueSpotlight;
